mapper.select:
SELECT a.*,p.`text` as typename,c.name as product_name FROM m_product_specs AS a LEFT JOIN m_product_type p on p.id=a.type_id LEFT JOIN m_product c on c.id=a.product_id where 1=1 and a.is_delete='N'
and a.type_id=#{type_id}
limit #{start},#{end}

SELECT a.*
FROM m_product_specs AS a where 1=1 and a.is_delete='N'
and a.product_id=#{product_id}
limit #{start},#{end}

SELECT count(1) as total FROM m_product_specs AS a where 1=1 and a.is_delete='N'
and a.product_id=#{product_id}
limit #{start},#{end}

SELECT a.*, b.`name` as specs_name,b.id as bid, c. name as shop_name FROM m_product_specs_shop AS a LEFT JOIN m_product_specs AS b ON (a.specs_id = b.id) LEFT JOIN m_shop AS c ON (a.shop_id = c.id) where 1=1 and a.is_delete='N'
and a.specs_id=#{specs_id}
and a.shop_id=#{shop_id}
limit #{start},#{end}

SELECT count(1) as total FROM m_product_specs_shop AS a WHERE 1=1 and a.is_delete='N'
and a.specs_id=#{specs_id}
and a.shop_id=#{shop_id}
